import { Link } from 'react-router-dom'
import './nav.scss'

function nav() {
    const currentRoute = window.location.pathname;

      return (
            <nav className="navList">
                  <ul className="nav_list">
                        <li
                              className={
                                    currentRoute === '/'
                                          ? 'nav_list_item_active'
                                          : 'nav_list_item'
                              }
                        >
                              <Link to="/Kasa/">Accueil</Link>
                        </li>
                        <li
                              className={
                                    currentRoute === '/about'
                                          ? 'nav_list_item_active'
                                          : 'nav_list_item'
                              }
                        >
                              <Link to="/about">A propos</Link>
                        </li>
                  </ul>
            </nav>
      )
}

export default nav
